import React, {Component} from "react";
import {withRouter} from "react-router-dom";
class Schoolselection extends Component {
    constructor() {
        super();
        this.state = {
            Recommenddata: [],
            Afternoondata: [],
            breakfastdata:[],
            Dessertdata:[]
        }
    }
    async componentDidMount(){
        // 推荐课程
        const data = await this.$axios.get("/recommend/getRandContent?_t=1587794086105&csrfToken=eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJhdWQiOjAsImV4cCI6MTc3NzA5NjA1MSwiaWF0IjoxNTg3NzkzNjUxfQ.mLnoB1Hs-bdJyPJYcVqXA9__Rd4aL13wQWYd7AsnJ2g&type=3&pageSize=10");
        this.setState({
            Recommenddata:data.data.data

        })
        //下午时光光茶
        const Afternoondata = await this.$axios.get("/recommend/getRandContent?_t=1587795934878&csrfToken=eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJhdWQiOjAsImV4cCI6MTc3NzA5NjA1MSwiaWF0IjoxNTg3NzkzNjUxfQ.mLnoB1Hs-bdJyPJYcVqXA9__Rd4aL13wQWYd7AsnJ2g&type=3&pageSize=10");
            this.setState({
                Afternoondata:Afternoondata.data.data
            })
        // console.log(this.state.Recommenddata)
        const breakfastdata = await this.$axios.get("/recommend/getRandContent?_t=1587793664393&csrfToken=eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJhdWQiOjAsImV4cCI6MTc3NzA5NjA1MSwiaWF0IjoxNTg3NzkzNjUxfQ.mLnoB1Hs-bdJyPJYcVqXA9__Rd4aL13wQWYd7AsnJ2g&type=3&pageSize=10");
            this.setState({
                breakfastdata:breakfastdata.data.data
            })
        const Dessertdata = await this.$axios.get("//recommend/getRandContent?_t=1587794086105&csrfToken=eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJhdWQiOjAsImV4cCI6MTc3NzA5NjA1MSwiaWF0IjoxNTg3NzkzNjUxfQ.mLnoB1Hs-bdJyPJYcVqXA9__Rd4aL13wQWYd7AsnJ2g&type=3&pageSize=10");
            this.setState({
                Dessertdata:Dessertdata.data.data
            })

    }
    render(){
        return (
            <div className={"Schoolselection"}>
                <p>学堂精选</p>
                <p>推荐课程</p>
                <div className={"mid"}>{
                    this.state.Recommenddata.map((v,i)=>{
                        return (<div className={"movie"} key={i}> <img  alt={"标本"} src={v.coverImage} />
                        <p>{v.coverTitle}</p>
                        </div> )
                    })
                }
                </div>
                <p>下午时光茶</p>
                <div className={"mid"}>{
                    this.state.Afternoondata.map((v,i)=>{
                        return (<div className={"movie"} key={i}> <img  alt={"标本"} src={v.coverImage} />
                            <p>{v.coverTitle}</p>
                        </div> )
                    })
                }
                </div>
                <p>元气早餐</p>
                <div className={"mid"}>{
                    this.state.Dessertdata.map((v,i)=>{
                        return (<div className={"movie"} key={i}> <img  alt={"标本"} src={v.coverImage} />
                            <p>{v.coverTitle}</p>
                        </div> )
                    })
                }
                </div>
                <p>炎夏甜点</p>
                <div className={"mid"}>{
                    this.state.breakfastdata.map((v,i)=>{
                        return (<div className={"movie"} key={i}> <img  alt={"标本"} src={v.coverImage} />
                            <p>{v.coverTitle}</p>
                        </div> )
                    })
                }
                </div>
            </div>
        )
    }
}

export default withRouter(Schoolselection);
