import React, {Component} from "react";
import {withRouter} from "react-router";
import {Upload,Dialog,i} from "element-react";
class index extends Component {
    constructor(props) {
        super(props);
        this.state = {
            dialogImageUrl: '',
            dialogVisible: false
        }
    }
    add=()=>{
        this.props.history.go(-1)
    }
    render() {
        const {dialogImageUrl, dialogVisible} = this.state;
        return (
            <div>
                <i onClick={this.add} className="el-icon-arrow-left"></i>
            <div className={"Uploadleft"}>
                <Upload
                    action="//jsonplaceholder.typicode.com/posts/"
                    listType="picture-card"
                    onPreview={file => this.handlePictureCardPreview(file)}
                    onRemove={(file, fileList) => this.handleRemove(file, fileList)}
                >
                    <i className="el-icon-plus"></i>
                </Upload>
                <Dialog
                    visible={dialogVisible}
                    size="tiny"
                    onCancel={() => this.setState({dialogVisible: false})}
                >
                    <img width="100%" src={dialogImageUrl} alt=""/>
                </Dialog>
                <span>上传食谱</span>
                </div>
                <div className={"Uploadright"}>
                <Upload
                action="//jsonplaceholder.typicode.com/posts/"
                listType="picture-card"
                onPreview={file => this.handlePictureCardPreview(file)}
                onRemove={(file, fileList) => this.handleRemove(file, fileList)}
                >
                <i className="el-icon-plus"></i>
                </Upload>
                <Dialog
                visible={dialogVisible}
                size="tiny"
                onCancel={() => this.setState({dialogVisible: false})}
                >
                <img width="100%" src={dialogImageUrl} alt=""/>
                </Dialog>
                <span>上传作品</span>
                </div>
                </div>
        )
    }

    handleRemove(file, fileList) {
        console.log(file, fileList);
    }

    handlePictureCardPreview(file) {
        this.setState({
            dialogImageUrl: file.url,
            dialogVisible: true,
        })
    }

}
export default withRouter(index) ;
