import React, {Component,Fragment} from 'react';
import HeadSearch from "../top/HeadSearch";
export default class HaHa extends Component{
    constructor(props) {
        super(props);
        this.state={
            data:[],
            content:[],
            title:[]
        };
    }
    async getList() {
        const {data}= await axios.get("/api/education/getIndexByWeb?_t="+Date.now());
        data.data.category.splice(0,1)
        this.setState({
            data:data.data.category,
        }
        )

    }
    async getContent(){
        const content=await axios.get("/api/recommend/getRandContent?_t=1587794086105&csrfToken=eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJhdWQiOjAsImV4cCI6MTc3NzA5NjA1MSwiaWF0IjoxNTg3NzkzNjUxfQ.mLnoB1Hs-bdJyPJYcVqXA9__Rd4aL13wQWYd7AsnJ2g&type=3&pageSize=10")
            this.setState({
                content:content.data.data.data
            })
    }
    My(){
        this.props.history.push('/buyDetai')
    }
    async componentDidMount() {
        const title = await axios.get('/api/index/get?_t=1588174976854&');
        this.setState({
            title:title.data.data.category[0].item.map(v=>{
                return v
            })
        })

        this.getList()
        this.getContent()
    }


    render() {
        return (

            <Fragment>
                <HeadSearch></HeadSearch>
                <div className={"nav-index"}>
                    {
                        this.state.title.map((v,i)=>{
                            return (<span key={i}>  <img alt={"标本"} src={v.image} />{v.title}</span>)
                        })

                    }
                </div>
                <div className={"max"} >
                    <div style={{marginTop:"40px"}} className={"title"}><span>{"推荐课程"}</span></div>
                    <div className={"movie"}>
                            {
                                this.state.content.map(i=>(
                                    <a onClick={this.My.bind(this)} key={i.contentId}>
                                        <img src={i.coverImage} alt=""/>
                                        <p>{i.coverTitle}</p>
                                    </a>
                                ))
                            }
                    </div>
                </div>
                    {
                        this.state.data.map(v => (
                            <div className={"max"} key={v.categoryId}>
                                <div className={"title"}><span>{v.title}</span></div>
                                    <div className={"movie"}>
                                        {
                                            v.item.map(i=>(
                                                <a href="#" key={i.categoryItemId}>
                                                    <img src={i.image} alt=""/>
                                                    <p>{i.title}</p>
                                                </a>
                                            ))
                                        }
                                    </div>
                            </div>
                        ))
                    }
            </Fragment>

        )
    }
}
