import React, {Component} from "react";
import {withRouter} from "react-router";
import {i, Input} from "element-react"
class Anput extends Component {
    constructor() {
        super();
        this.state = {}
    }
    add=()=>{
       this.props.history.push("/server")
    }
    render() {
        return (<div className={"input"}>
            <Input
                onFocus={this.add}
                type="textarea"
                autosize={true}
                placeholder="    搜索食材/食谱......./一应俱全"
            /> <i className="el-icon-search"></i>
        </div>)
    }

}

export default withRouter(Anput) ;
