import React, {Component, Fragment} from "react";
import {withRouter} from "react-router";
import {i, Tag,Carousel} from "element-react"
class index extends Component {
    constructor() {
        super();
        this.state = {
            data:[],
            img:[],
            datas:[],
            imgs:[],
            v:[]
        }
    }

    async componentDidMount() {
        const data=await this.$axios.get("/index/getByType?_t=1587994643480&csrfToken=eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJhdWQiOjAsImV4cCI6MTc3NzA5NjA1MSwiaWF0IjoxNTg3NzkzNjUxfQ.mLnoB1Hs-bdJyPJYcVqXA9__Rd4aL13wQWYd7AsnJ2g&type=11");
        this.setState({
            data:data.data
        })
        const img = await  this.$axios.get("/education/getIndexByWeb?");
        this.setState({
            img:img.data.category[0].item
        })
    }

    render() {
        return (<Fragment>
            <div className={"taddd"}>
                {
                    this.state.data.map((v,i)=>{
                        return (<div className={"midx"} key={i}><Tag className={"moviep"} >{v.title}</Tag> </div>)
                    })
                }
                <div className={"ii"}><i  className="el-icon-search "></i></div>
                <div className={"tion"}>
                    <div className="demo-2 medium">
                        <Carousel indicatorPosition="outside" interval="4000">
                            {
                                this.state.img.map((item, index) => {
                                    return (
                                        <Carousel.Item key={index}>
                                            <img className={"tionimg"} alt={"标本"}  src={item.image}/>
                                        </Carousel.Item>
                                    )
                                })
                            }
                        </Carousel>
                    </div>
                </div>
            </div>
        </Fragment>)
    }

}

export default withRouter(index) ;
