import React, {Component, Fragment} from "react";
import {withRouter} from "react-router";
import {Carousel} from "element-react";
class Newest extends Component {
    constructor() {
        super();
        this.state = {
            data:[],
            image:[]
        }
    }
    async componentDidMount(){
        const data = await  this.$axios.get("/api/feed/getCategory?_t=1588387313263&csrfToken=eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJhdWQiOjAsImV4cCI6MTc3NzY4NzgxNywiaWF0IjoxNTg4Mzg1NDE3fQ.1zjEgQfQOMUS1z3i0CZoNulaEpYohIW5Os9-wqxZpZE");
        console.log(data.data.data.category.map(v=>{
            return v.item
        }));
        const index = 0;
        this.setState({
            data:data.data.data.category.map(v=>{
                return v.item
            })
        })
        this.setState({
            image:this.state.data[index].map(v=>{
                return v.image
            })
        })
       console.log(this.state.image)
    }
    render() {
        return (<Fragment>
            {/*轮播图*/}
            <div className={"Newest"}>
                <div className={"Newestone"}>
                    <div className="block">
                        <Carousel height="150px">
                            {
                                this.state.image.map((item, index) => {
                                    return (
                                        <Carousel.Item key={index}>
                                            <img alt={"标本"} src={item}/>
                                        </Carousel.Item>
                                    )
                                })
                            }
                        </Carousel>
                    </div>
                </div>
            </div>
        </Fragment>)
    }

}

export default withRouter(Newest);
