import React, {Component} from "react";
import {withRouter} from "react-router";
import {i} from "element-react";
class index extends Component {
    constructor() {
        super();
        this.state = {
            data:[]
        }
    }
    async componentDidMount() {
        const  data = await this.$axios.get("/education/getNewbieGuide?type=5&_t=1587993094325&csrfToken=eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJhdWQiOjAsImV4cCI6MTc3NzA5NjA1MSwiaWF0IjoxNTg3NzkzNjUxfQ.mLnoB1Hs-bdJyPJYcVqXA9__Rd4aL13wQWYd7AsnJ2g");
        // console.log(data.data.courseGuide)
        this.setState({
            data:data.data.courseGuide
        })
    }
    //image
    //title
    add=()=>{
        this.props.history.go(-1)
    }
    render() {
        return (<div>
            <i onClick={this.add} className="el-icon-arrow-left"></i>
            {
                this.state.data.map((v,i)=>{
                    return (<div className={"men"} key={i}>
                        <img className={"menimg"} alt={"标本"} src={v.image}/>
                        <div className={"mendiv"}>
                            <p className={"menp"}>{v.title}</p>
                        </div>
                    </div>)
                })
            }

        </div>)
    }

}

export default withRouter(index) ;
