import React, {Component, Fragment} from "react";
import {withRouter} from "react-router";
import color from "element-ui/packages/color-picker/src/color";
class Newest extends Component {
    constructor() {
        super();
        this.state = {
            data:[],
            image:[],
            li:[],
            color:[]
        }
        function rgb(){
            var r = Math.floor(Math.random()*256);
            var g = Math.floor(Math.random()*256);
            var b = Math.floor(Math.random()*256);
            var rgb = '('+r+','+g+','+b+')';
            return rgb;
        }
        function color16(){
            var r = Math.floor(Math.random()*256);
            var g = Math.floor(Math.random()*256);
            var b = Math.floor(Math.random()*256);
            var color = '#'+r.toString(16)+g.toString(16)+b.toString(16);
            return color;
        }
    }
    async componentDidMount(){
        const data = await  this.$axios.get("/api/feed/getCategory?_t=1588387313263&csrfToken=eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJhdWQiOjAsImV4cCI6MTc3NzY4NzgxNywiaWF0IjoxNTg4Mzg1NDE3fQ.1zjEgQfQOMUS1z3i0CZoNulaEpYohIW5Os9-wqxZpZE");
        const li=await this.$axios.get("/api/community/getByLimit?isShow=4&_t=1588407897276&csrfToken=eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJhdWQiOjAsImV4cCI6MTc3NzY4NzgxNywiaWF0IjoxNTg4Mzg1NDE3fQ.1zjEgQfQOMUS1z3i0CZoNulaEpYohIW5Os9-wqxZpZE&pageIndex=0&pageSize=99")
        const index = 0;
        this.setState({
            data:data.data.data.category.map(v=>{
                return v.item
            }),
            li:li.data.data.data.map(v=>{
                return v.name
            }),
        })
        this.setState({
            image:this.state.data[index]
        })
        this.setState({
            color:color16()
        })
        // this.refs.color.style.backgroundColor =this.state.color;

    }
    render() {
        return (<Fragment>
            {/*轮播图*/}
            <div className={"Newest"}>
                <div className={"Newestone"}>
                            {
                                this.state.image.map((v, i) => {
                                    return (<div key={i}><img alt={"标本"} src={v.image}/></div>)

                                })
                            }
                </div>
                <div className={"Newesttwo"}>
                    {
                        this.state.li.map((v,i)=>{
                            return (<ul key={i}><li ref={"color"} >{v}</li></ul>)
                        })
                    }
                </div>
            </div>
        </Fragment>)
    }

}

export default withRouter(Newest);
