import React, {Component} from "react";
import {Input} from "element-react";
class INput extends Component {
    constructor() {
        super();
        this.state = {}
    }

    render() {
        return (
            <div className={"top-input"}>
                <Input
                    type="textarea"
                    autosize={{ minRows: 1, maxRows: 4}}
                    placeholder="请输入内容"
                />
            </div>
        )}

}

export default INput;
